import { useState, useEffect } from "react";
import Lottie from "lottie-react";
import "./hero.css";
import devAnimation from "../../animation/dev.json";
import { Link } from "react-router-dom";
import LazyLoad from "react-lazyload";

const Hero = () => {
  const [theme, setTheme] = useState("dark");

  // قراءة الـ theme مرة واحدة عند تحميل المكون
  useEffect(() => {
    const storedTheme = localStorage.getItem("currentMode");
    if (storedTheme) {
      setTheme(storedTheme);
    }
  }, []);

  // دالة لتغيير الوضع مباشرة
  const toggleTheme = () => {
    const newTheme = theme === "dark" ? "light" : "dark";
    setTheme(newTheme);
    localStorage.setItem("currentMode", newTheme);
  };

  return (
    <section className={`hero flex ${theme}`}>
      <div className="left-section">
        <button onClick={toggleTheme}>
          Switch to {theme === "dark" ? "Light" : "Dark"} Mode
        </button>

        <div className="parent-avatar flex">
          <LazyLoad height={200} once offset={100}>
            <img
              src="/img/Myphoto.png"
              className="avatar"
              alt="MyPhoto"
              loading="lazy"
            />
          </LazyLoad>
          <div className="icon-verified"></div>
        </div>

        <h1 className="title">
          Software designer, founder, and amateur astronaut.
        </h1>

        {/* DEV ICONS */}
        <div className="devicon flex gap-6 text-5xl flex-wrap items-center mt-10">
          <i className="devicon-npm-plain colored"></i>
          <i className="devicon-react-original colored"></i>
          <i className={`devicon-nextjs-plain ${theme === "light" ? "colored" : ""}`}></i>
          <i className="devicon-javascript-plain colored"></i>
          <i className="devicon-typescript-plain colored"></i>
          <i className={`devicon-redux-original ${theme === "light" ? "colored" : "colored"}`}></i>
          <i className="devicon-mongodb-plain colored"></i>
          <i className="devicon-nodejs-plain-wordmark colored"></i>
          <i className={`devicon-express-original ${theme === "light" ? "colored" : ""}`}></i>
          <i className={`devicon-mysql-original ${theme === "light" ? "colored" : ""}`}></i>
          <i className={`devicon-microsoftsqlserver-plain ${theme === "light" ? "colored" : "colored"}`}></i>
          <i className={`devicon-postman-plain ${theme === "light" ? "colored" : "colored"}`}></i>
          <i className="devicon-tailwindcss-plain colored"></i>
          <i className="devicon-sass-original colored"></i>
          <i className={`devicon-figma-plain ${theme === "light" ? "colored" : "colored"}`}></i>
        </div>
      </div>

      {/* ANIMATION */}
      <div className="right-section animation">
        <Lottie
          animationData={devAnimation}
          loop
          style={{ width: "100%", height: "auto" }}
        />
      </div>
    </section>
  );
};

export default Hero;
